query Client($id: Int!) {
  client(id: $id) {
    __typename
    name
    address
    email
    details
  }
}

bf5ca550808244ee1f3b081802c481bb3368d1eb.source:
mutation CreateClient($input: ClientInput) {
  createClient(input: $input) {
    __typename
    id
    name
  }
}

905cb83cae8134ad316a26a1ce66aa404cae1392.source:
mutation CreateEmployee($input: EmployeeInput!) {
  createEmployee(input: $input) {
    __typename
    id
    employee_name
    employee_code
    account {
      __typename
      id
      firstname
      lastname
      email
    }
    role {
      __typename
      role_name
    }
  }
}

