dfc5b789ea03265d608a7508d7bc75006b61d35d.source:
query Client($id: Int!) {
  client(id: $id) {
    __typename
    name
    address
    email
    details
  }
}

bf5ca550808244ee1f3b081802c481bb3368d1eb.source:
mutation CreateClient($input: ClientInput) {
  createClient(input: $input) {
    __typename
    id
    name
  }
}

1d4724921e3778cb05f44e68dbf1f745160bca2b.source:
query Employee($id: Int!) {
  employee(id: $id) {
    __typename
    employee_name
    employee_code
    account {
      __typename
      firstname
      lastname
      email
    }
    role {
      __typename
      role_name
    }
  }
}

db28f1c8123316dad1f800c4225ccf13af7683a0.source:
mutation UpdateClient($id: Int!, $input: ClientUpdateInput!) {
  updateClient(id: $id, input: $input)
}
